import React, { useEffect, useState } from "react";
import { useParams } from 'react-router-dom';
import NavLink from "react-bootstrap/esm/NavLink";

const MovieDetailsPage = () => {
    const { id } = useParams();
    const [movie, setMovie] = useState(null);

   

    const getMovieDetails = async (movieId) => {
        try {
            const resp = await fetch('/movies/:movieId');
            const data = await resp.json();
            console.log(data);
            setMovie(data);
        }
        catch (error) {
            console.log("Error retreiving movie: ", error)
        }

    }

    useEffect(() => {
        getMovieDetails()
    })
    
    return (
        <section className="movie-section">
            <div className="movie-card">
                <figure>
                    <img src={`https://image.tmdb.org/t/p/w300/${movie}`} alt="movie poster" />
                </figure>
                <div className="card-content">
                    <p className="title">{movie}</p>
                    <p className=""></p>
                    <p className="card-text">{movie}</p>
                    <p className="card-text">{movie}</p>
                    <p className="card-text">{movie} / 10</p>
                    <p className="card-text">{movie}</p>
                    <NavLink to="/" className="back-btn">
                        Go Back
                    </NavLink>
                </div>
            </div>
        </section>
    );
}

export default MovieDetailsPage;
