import React, {useEffect, useState} from 'react';
import axios from 'axios';


function App() {

  const[movieData, setMovieData] = useState([{}])

  useEffect(()=>{
    getmovieData("movie");

  }, [])

  async function getMovieData(type){
    try{
      const apiKey ='2ca07079918fc4cc9398ada6f5671ac6';
      let resp = await axios.get(`https://api.themoviedb.org/3/trending/${type}/day?api_key=${apiKey}&media_type=movie`);
      console.log(21, resp.data.results);

      setMovieData(resp.data.results);
    } catch (e){

    }finally{

    }

  }


  return (
    <div>

      {(typeof movieData.users === 'undefined')? (
        <p>Loading...</p>
      ): (
       movieData.users.map((user, i)=>(
          <div className="background_container">
            <div className="button_container">
              <button onClick={()=>{
                getMovieData("movie");
              }
              }>
                Movies
              </button>
              <button onClick={()=>{
                getMovieData("tv");
              }
              }>
                Tv Shows
              </button>
            </div>
            <div className='flex-container'>
              {movieData.map((item)=>
                <div className='movie_item'>
                <img src={`https://image.tmdb.org/t/p/w300/${item.poster_path}`}/>           

            <div className='movie_name'>
              {item.original_title ? item.original_title :item.original_title}
              </div>

          </div>

        )}
          </div>
          </div>
      ))

    )}
    </div>
  )};  


export default App
